def main(command_tram,bytes_to_read):


    ser = serial.Serial('COM3', 38400 , EIGHTBITS ,PARITY_NONE, STOPBITS_ONE, timeout=1000)  # open serial port COM 3 wiyh it's config, timaout is just a security, it's not important though
    print(ser.name)         # check which port was really used
    
    ser.write(bytearray.fromhex(command_tram))
         # write a string
    c = ser.read(bytes_to_read)
    int_table = list(c)
    hexa_list = [hex(val) for val in int_table]
    print(hexa_list)
    


    ser.close()             # close port
    
def application_offset_addr(addr_offset,addr):
    # -----------------------conversion for addresse-----------------------
        decimal1 = int.from_bytes(bytes.fromhex(addr_offset)[::-1], byteorder='big')
        decimal2 = int.from_bytes(bytes.fromhex(addr)[::-1], byteorder='little')


        # Somme des entiers
        int_actual_address = decimal1 + decimal2
        hex_actual_address = int_actual_address.to_bytes(4, byteorder='little').hex()[::1]

        return hex_actual_address


def application_transmit(bigdata,addr_offset):

    list_of_length_of_segmented_data = [] # used to get the length of all the data send in byte
    test_list_1 = []
    test_list_2 = []
    sous_chaines = bigdata.split(":") # split the main hex files in smaller pieces

    for i in sous_chaines:

        #----------------recupération de informations sur le HEX-------------------------
        length_data_to_transmit = i[0:2]
        #length_data_to_read = int.from_bytes(bytes.fromhex(length_data_to_transmit)) + 1
        addr = "0000" +i[2:6] #convertion en 16 bites 
        data = i[6:]
        decimal2 = int.from_bytes(bytes.fromhex(addr)[::-1], byteorder='little')
        hex_addr =  decimal2.to_bytes(4, byteorder='little').hex()[::1]

        #création de l'addresse avec l'offset application
        #hex_offseted_addr = application_offset_addr(addr_offset, addr)
        
        tram_main_write = "02"+length_data_to_transmit+"00"+"5500AA00"+hex_addr+data
        #tram_main_read = "03"+length_data_to_transmit+"005500AA"+hex_offseted_addr
        main(tram_main_write,0)
        
        #main(tram_main_read,length_data_to_read)

        
        list_of_length_of_segmented_data.append(length_data_to_transmit)
        #test_list_1.append(hex_offseted_addr)
        #test_list_1.append(data)
        test_list_1.append(hex_addr)
        #print(length_data_to_transmit+"00")

        
        #test_list_2.append(hex_offseted_addr)

    #print(list_of_length_of_segmented_data)
    print(test_list_1,test_list_2)
    #print("let' go reset")
    main("0900000000000000000000",37)
